/**A reducer always takes the intial state 
 and the action 
 */
import { Action_types } from "../Constants/Action_types"

const intialState = {
    products: [{
        id:1,
        title: "Dipesh",
        category: "Programmer",
    }]
};

 export const productReducer = (state = intialState, {types, payload}) => {
        switch ( types) {
            case Action_types.SET_PRODUCTS:
                return state;
                default:
                    return state;

        }
 }
